$script1Path = "C:\Users\Rayane\OneDrive\Bureau\Python\auto.py"
$script2Path = "C:\Users\Rayane\OneDrive\Bureau\Python\dashboard.py"

# Create a new scheduled task for the first Python script
$action1 = New-ScheduledTaskAction -Execute "C:\Windows\System32\WindowsPowerShell\v1.0\powershell.exe" -Argument "-ExecutionPolicy Bypass -File `"$script1Path`""
$trigger1 = New-ScheduledTaskTrigger -Once -At (Get-Date).Date -RepetitionInterval (New-TimeSpan -Hours 1) -RepetitionDuration (New-TimeSpan -Days 1)
Register-ScheduledTask -TaskName "Python Script 1 Hourly Task" -Action $action1 -Trigger $trigger1 -Description "Task to run auto.py every hour"

# Create a new scheduled task for the second Python script
$action2 = New-ScheduledTaskAction -Execute "C:\Windows\System32\WindowsPowerShell\v1.0\powershell.exe" -Argument "-ExecutionPolicy Bypass -File `"$script2Path`""
$trigger2 = New-ScheduledTaskTrigger -Once -At (Get-Date).Date -RepetitionInterval (New-TimeSpan -Hours 1) -RepetitionDuration (New-TimeSpan -Days 1)
Register-ScheduledTask -TaskName "Python Script 2 Hourly Task" -Action $action2 -Trigger $trigger2 -Description "Task to run dashboard.py every hour"
